<?php
/**
 * Homepage Banner Block
 *
 * @package      NWU2025
 * @author       Debbie Labedz
 * @since        1.0.0
 * @license      GPL-2.0+
 **/

// Create id attribute allowing for custom "anchor" value.
$id = 'homepage-banner-' . $block['id'];
if (!empty($block['anchor'])) {
    $id = $block['anchor'];
}

// Create class attribute allowing for custom "className" and "align" values.
$classes = ['homepage-banner'];
if (!empty($block['className'])) {
    $classes[] = $block['className'];
}
if (!empty($block['align'])) {
    $classes[] = 'align' . $block['align'];
}

// Load values
$heading = get_field('heading');
$subheading = get_field('subheading');
$button = get_field('button');
$show_lines = get_field('show_lines');
$show_badge = get_field('show_badge');

// Show placeholder if no content
if (empty($heading)) {
    echo '<div style="padding: 40px; background: #f0f0f0; border: 2px dashed #ccc; text-align: center; border-radius: 8px;">';
    echo '<p style="margin: 0; font-size: 16px; color: #666;"><strong>Homepage Banner Block</strong></p>';
    echo '<p style="margin: 8px 0 0; font-size: 14px; color: #999;">Add content to the fields above to see your banner.</p>';
    echo '</div>';
    return;
}
?>
<section id="<?php echo esc_attr($id); ?>" class="<?php echo esc_attr(implode(' ', $classes)); ?>">
    <div class="homepage-banner__container">
        <div class="homepage-banner__content">
            <h1 class="homepage-banner__heading"><?php echo esc_html($heading); ?></h1>

            <?php if (!empty($subheading)): ?>
                <div class="homepage-banner__subheading">
                    <?php echo wp_kses_post($subheading); ?>
                </div>
            <?php endif; ?>

            <?php if (!empty($button['url'])): ?>
                <a href="<?php echo esc_url($button['url']); ?>"
                   class="homepage-banner__button wp-element-button"
                   <?php if (!empty($button['target'])): ?>target="<?php echo esc_attr($button['target']); ?>"<?php endif; ?>
                   <?php if (!empty($button['target']) && $button['target'] === '_blank'): ?>rel="noopener noreferrer"<?php endif; ?>>
                    <?php echo esc_html($button['title']); ?>
                </a>
            <?php endif; ?>
        </div>

        <?php if ($show_badge): ?>
