<?php
/**
 * Page Banner Block
 *
 * @package      NWU2025
 * @author       Debbie Labedz
 * @since        1.0.0
 * @license      GPL-2.0+
 **/

// Get ACF fields
$title      = get_field( 'title' );
$text       = get_field( 'text' );
$button     = get_field( 'button' );
$show_badge = get_field( 'show_badge' );

// Build classes
$classes = ['page-banner'];
if ( ! empty( $block['align'] ) ) {
	$classes[] = 'align' . $block['align'];
}
if ( $show_badge ) {
	$classes[] = 'page-banner--with-badge';
}

// Build anchor
$anchor = '';
if ( ! empty( $block['anchor'] ) ) {
	$anchor = ' id="' . esc_attr( $block['anchor'] ) . '"';
}
?>

<div class="<?php echo esc_attr( join( ' ', $classes ) ); ?>"<?php echo $anchor; ?>>
	<div class="page-banner__inner">
		<div class="page-banner__content">
			<?php if ( ! empty( $title ) ) : ?>
				<h1 class="page-banner__title"><?php echo esc_html( $title ); ?></h1>
			<?php endif; ?>

			<?php if ( ! empty( $text ) ) : ?>
				<div class="page-banner__text">
					<?php echo wp_kses_post( $text ); ?>
				</div>
			<?php endif; ?>

			<?php if ( ! empty( $button ) && ! empty( $button['url'] ) ) : ?>
				<div class="page-banner__button">
					<?php echo be_button( $button ); ?>
				</div>
			<?php endif; ?>
		</div>

